(8, 1, 'primary_attendee', 'content', 'a:3:{s:4:"main";s:1953:"<!-- HEADER -->\n<table class="head-wrap" bgcolor="#999999">\n<tbody>\n<tr>\n<td></td>\n<td class="header container">\n<div class="content">\n<table bgcolor="#999999">\n<tbody>\n<tr>\n<td>[CO_LOGO]</td>\n<td align="right">\n<h6 class="collapse">[COMPANY]</h6>\n</td>\n</tr>\n</tbody>\n</table>\n</div></td>\n<td></td>\n</tr>\n</tbody>\n</table>\n<!-- END HEADER -->\n\n<!-- BODY -->\n<table class="body-wrap">\n<tbody>\n<tr>\n<td></td>\n<td class="container" bgcolor="#FFFFFF"><!-- content -->\n<div class="content">\n<table>\n<tbody>\n<tr>\n<td>\n<h2>Hello, [PRIMARY_REGISTRANT_FNAME] [PRIMARY_REGISTRANT_LNAME]:</h2>\n<p class="lead">We''re just notifying you of a successful payment made for the following transaction and tickets:</p>\n\n<h3>Payment Details:</h3>\n<ul>\n <li><strong>Payment Status:</strong> [PAYMENT_STATUS]</li>\n  <li><strong>Transaction ID:</strong> [TXN_ID]</li>\n  <li><strong>Total Cost:</strong> [TOTAL_COST]</li>\n  <li><strong>Payment Amount:</strong> [AMOUNT_PAID]</li>\n <li><strong>Amount Due:</strong> [TOTAL_OWING]</li>\n</ul>\n[EVENT_LIST]\n</td>\n</tr>\n</tbody>\n</table>\n</div></td>\n<td></td>\n</tr>\n</tbody>\n</table>\n<!-- END BODY -->\n\n<!-- FOOTER -->\n<table class="footer-wrap">\n<tbody>\n<tr>\n<td class="container">\n<table class="social" width="100%">\n<tbody>\n<tr>\n<td><!-- column 1 -->\n<table class="column" align="left">\n<tbody>\n<tr>\n<td>\n<h3>Connect with Us:</h3>\n<a class="soc-btn fb" href="[CO_FACEBOOK_URL]">Facebook</a> <a class="soc-btn tw" href="[CO_TWITTER_URL]">Twitter</a> <a class="soc-btn gp" href="[CO_GOOGLE_URL]">Google+</a></td>\n</tr>\n</tbody>\n</table>\n<!-- /column 1 -->\n\n<!-- column 2 -->\n<table class="column" align="left">\n<tbody>\n<tr>\n<td>\n<h3>Contact Info:</h3>\nPhone: <strong>[CO_PHONE]</strong>\n\nEmail: <strong><a href="mailto:[CO_EMAIL]" target="_blank">[CO_EMAIL]</a></strong></td>\n</tr>\n</tbody>\n</table>\n<!-- /column 2 -->\n\n </td>\n</tr>\n</tbody>\n</table>\n<!-- end social table --></td>\n<td></td>\n</tr>\n</tbody>\n</table>\n<!-- /FOOTER -->\n";s:10:"event_list";s:150:"<!-- Callout Panel -->\n<p class="callout"><strong>Event: [EVENT_LINK]</strong></p>\n<!-- /Callout Panel -->\n<div class="content">\n [TICKET_LIST]\n</div>";s:11:"ticket_list";s:237:"<ul>\n<li class="ticket-name"><strong>Ticket Name:</strong> [TICKET_NAME]<br><em>[TICKET_DESCRIPTION]</em></li>\n<li><strong>Price:</strong> [TICKET_PRICE]</li>\n<li><strong>Quantity Purchased:</strong> [TKT_QTY_PURCHASED]</li>\n</ul>\n<hr />";}'),
