(204, 'mc4wp_lite_form', 'a:11:{s:3:"css";s:7:"default";s:5:"lists";a:1:{i:8234895958;s:10:"8234895958";}s:6:"markup";s:281:"<h4>Round Hex Newsletter</h4>\r\n<p>Keep up to date with Round Hex with quarterly newsletters about our latest projects.</p>\r\n <input type="email" id="mc4wp_email" name="EMAIL" placeholder="Enter Email Address" required />\r\n <input id="mc_submit" type="submit" value="Subscribe" />\r\n";s:12:"double_optin";i:1;s:18:"hide_after_success";i:0;s:8:"redirect";s:0:"";s:12:"text_success";s:79:"Thank you, your sign-up request was successful! Please check your e-mail inbox.";s:18:"text_invalid_email";s:37:"Please provide a valid email address.";s:27:"text_required_field_missing";s:35:"Please fill in the required fields.";s:23:"text_already_subscribed";s:53:"Given email address is already subscribed, thank you!";s:10:"text_error";s:51:"Oops. Something went wrong. Please try again later.";}', 'yes'),
